import React from 'react';

class ShipSelector extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            shipSelected: null,
            isCompressed: true
        }
    }
    handleClick = () =>{
        this.setState({isCompressed: false})
    }
    chooseGolem = () => {
        this.setState({shipSelected: "Golem"})
    }
    render() {
        return(
        this.state.isCompressed ?  (
            <button onClick = {this.handleClick}>Select Targets</button>
        ) :
        (
            <div>
                <button onClick = {this.chooseGolem}>Golem</button>
                <button onClick = {this.chooseGolem}>Paladin</button>
                <button onClick = {this.chooseGolem}>Vargur</button>
                <button onClick = {this.chooseGolem}>Kronos</button>
                <button onClick = {this.chooseGolem}>Revelation</button>
                <button onClick = {this.chooseGolem}>Phoenix</button>
                <button onClick = {this.chooseGolem}>Moros</button>
                <button onClick = {this.chooseGolem}>Naglfar</button>
                <button onClick = {this.chooseGolem}>Gila</button>
                <button onClick = {this.chooseGolem}>Praxis</button>
                <button onClick = {this.chooseGolem}>Nestor</button>
                <button onClick = {this.chooseGolem}>Leshak</button>
                <button onClick = {this.chooseGolem}>Rattlesnake</button>
                <button onClick = {this.chooseGolem}>Heron</button>
            </div>
        )
        )
    }
}

export default ShipSelector;
