import React from 'react';

class ShipSelector extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            expanded: false
        }
        this.handleClick = this.handleClick.bind(this);
    }
    handleClick(){
        this.setState({expanded: 'true'})
    }
    render() {
        return (
            <div class="dropdown">
                <button onClick = {this.handleClick} className="btn btn-secondary dropdown-toggle" type="button" id="dropdownMenuButton" data-toggle="dropdown" aria-haspopup="true" aria-expanded={this.state.expanded}>
                    Dropdown button
                </button>
                <div className="dropdown-menu" aria-labelledby="dropdownMenuButton">
                    <a className="dropdown-item" href="#">Action</a>
                    <a className="dropdown-item" href="#">Another action</a>
                    <a className="dropdown-item" href="#">Something else here</a>
                </div>
            </div>
        )
    }
}


export default ShipSelector;
