import React from 'react';

class ShipSelector extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            shipSelected: null,
            isCompressed: true
        }
    }
    handleClick = () =>{
        this.setState({isCompressed: false});
        document.addEventListener('click', () => {
            this.setState({isCompressed : true})
        })
    }
    chooseGolem = () => {
        this.setState({shipSelected: "Golem"})
    }
    choosePaladin = () => {
        this.setState({shipSelected: "Paladin"})
    }
    chooseVargur = () => {
        this.setState({shipSelected: "Vargur"})
    }
    chooseKronos = () => {
        this.setState({shipSelected: "Kronos"})
    }
    chooseRevelation = () => {
        this.setState({shipSelected: "Revelation"})
    }
    choosePhoenix = () => {
        this.setState({shipSelected: "Phoenix"})
    }
    chooseMoros = () => {
        this.setState({shipSelected: "Moros"})
    }
    chooseNaglfar = () => {
        this.setState({shipSelected: "Naglfar"})
    }
    chooseGila = () => {
        this.setState({shipSelected: "Gila"})
    }
    choosePraxis = () => {
        this.setState({shipSelected: "Praxis"})
    }
    chooseNestor = () => {
        this.setState({shipSelected: "Nestor"})
    }
    chooseLeshak = () => {
        this.setState({shipSelected: "Leshak"})
    }
    chooseRattlesnake = () => {
        this.setState({shipSelected: "Rattlesnake"})
    }
    chooseHeron = () => {
        this.setState({shipSelected: "Heron"})
    }
    render() {
        return(
        this.state.isCompressed ?  (
            <button onClick = {this.handleClick}>Select Targets</button>
        ) :
        (
            <div>
                <button onClick = {this.chooseGolem}>Golem</button>
                <button onClick = {this.choosePaladin}>Paladin</button>
                <button onClick = {this.chooseVargur}>Vargur</button>
                <button onClick = {this.chooseKronos}>Kronos</button>
                <button onClick = {this.chooseRevelation}>Revelation</button>
                <button onClick = {this.choosePhoenix}>Phoenix</button>
                <button onClick = {this.chooseMoros}>Moros</button>
                <button onClick = {this.chooseNaglfar}>Naglfar</button>
                <button onClick = {this.chooseGila}>Gila</button>
                <button onClick = {this.choosePraxis}>Praxis</button>
                <button onClick = {this.chooseNestor}>Nestor</button>
                <button onClick = {this.chooseLeshak}>Leshak</button>
                <button onClick = {this.chooseRattlesnake}>Rattlesnake</button>
                <button onClick = {this.chooseHeron}>Heron</button>
            </div>
        )
        )
    }
}

export default ShipSelector;
