import React from 'react';

class ShipSelector extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            shipSelected: "Rattlesnake",
            showMenu: false
        }
        this.showMenu = this.showMenu.bind(this);
        this.closeMenu = this.closeMenu.bind(this);
    }
    showMenu(event) {
        event.preventDefault();

        this.setState({ showMenu: true }, () => {
          document.addEventListener('click', this.closeMenu);
        });
      }

      closeMenu(event) {
          if(!this.dropDownMenu.contains(event.target)){
            this.setState({ showMenu: false }, () => {
                document.removeEventListener('click', this.closeMenu);
              });
          }
      }
    render() {
        return (
            <div>
                <button onClick = {this.showMenu}>Ship Selector</button>
                {
                    this.state.showMenu ? (
                        <div className="menu"
                        ref = {(element) => {
                            this.dropDownMenu = element;
                        }}>
                            <button>Golem</button>
                            <button>Paladin</button>
                            <button>Vargur</button>
                            <button>Kronos</button>
                            <button>Revelation</button>
                            <button>Phoenix</button>
                            <button>Moros</button>
                            <button>Naglfar</button>
                            <button>Gila</button>
                            <button>Praxis</button>
                            <button>Nestor</button>
                            <button>Leshak</button>
                            <button>Rattlesnake</button>
                        </div>
                    ) : (
                            null
                        )
                }
            </div>
        )
    }
}
