import React from 'react';
import axios from 'axios';

class Info extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            totalDestroyed: this.fetchTotalDestroyed(),
            shipSelected: this.HomeCheck(),
            totalDays: this.fetchTotalDays()
        };
        this.HomeCheck = this.HomeCheck.bind(this)
    }
    HomeCheck = () => {
        if(window.location.pathname === "/"){
            return "Heron"
        }
        else {
            return window.location.pathname
        }
    }
    fetchTotalDestroyed(){
        axios.get(`/api/esi/totalDestroyed/${this.state.shipSelected}`)
        .then(response => {
            return this.setState({numberOfKills: response.data.totalDestroyed})
        })
    }
    fetchTotalDays(){
        axios.get(`/api/esi/totalDays`).then(response => {
            return this.setState({totalDays: response.data.totalDays})
        })
    }
    componentDidMount(){
        this.setState({shipSelected: window.location.pathname})
        this.fetchTotalDestroyed();
        this.fetchTotalDays();
    };
    render(){
            return (
                <p className = "info">Fetching {this.state.totalDestroyed} {this.state.shipSelected} kills in the last {this.state.totalDays} days.</p>
            )
    }
};

export default Info;
