import React from 'react';
import axios from 'axios';

class Unexpanded extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            weather: "Not yet gotten"
        }
    }
    handleButtonClick = () => {
        axios.get('/getWeatherInLondon').then(response => {
            this.setState({weather: response.data.temp})
        })
    };
    render(){
        return(
            <div>
                <button onClick = {this.handleButtonClick}>
                    Get weather in London
                </button>
                <h1>
                    The weather in London is: {this.state.weather}
                </h1>
            </div>
        )
    }
}


export default Unexpanded;
