import React from 'react';
import axios from 'axios';
import 'whatwg-fetch';


// axios.defaults.headers.get['Access-Control-Allow-Origin'] = 'true';

class Unexpanded extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            numberOfKills: null
        };
        }
        render(){
            return (
                <div>
                    <p className = 'noPad'>{this.props.name}</p>
                    <p>{this.state.numberOfKills}</p>
                    <button onClick = {this.handleClick}>click me</button>
                </div>
            )
        }
        handleClick = () => {
            axios.get('http://localhost:5000/api/esi/getNumKills').then(response => {
                this.setState({numberOfKills: response})
            })
        };
};

XPress.getSeries = () => {
    const url = `${baseUrl}/series`;
    return fetch(url).then(response => {
      if (!response.ok) {
        return new Promise(resolve => resolve([]));
      }
      return response.json().then(jsonResponse => {
        return jsonResponse.series.map(seriesItem => camelcaseKeys(seriesItem));
      });
    });
  };

export default Unexpanded;
