import React from 'react';
import axios from 'axios';

class Timeslot extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            numberOfKills: null,
            hotness: "Hourslot"
        };
    }
    componentDidMount = () => {
        axios.get(`/api/esi/${this.props.name}/${this.props.shipSelected}/${this.props.time}`)
            .then(response => {
                if(response.data.parsedData === 0){
                    return this.setState({numberOfKills: ''})
                }
                return this.setState({numberOfKills: response.data.parsedData})
            })
    };
    componentDidMount = () => {
        if(this.state.numberOfKills === 0){
            this.setState({hotness: "Hourslot"});
        } else if(this.state.numberOfKills <5){
            this.setState({hotness: "LowHeat"})
        }
    };
    render(){
            return (
                <td className = {this.state.hotness}>{this.state.numberOfKills}</td>
            )
    }
};

export default Timeslot;
