import React from 'react';
import Heatmap from './Heatmap';

class ShipSelector extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            shipSelected: null,
            isCompressed: true
        }
    }
    handleClick = () =>{
        this.setState({isCompressed: false});
    }
    chooseGolem = () => {
        this.setState({shipSelected: "Golem"})
    }
    choosePaladin = () => {
        this.setState({shipSelected: "Paladin"})
    }
    chooseVargur = () => {
        this.setState({shipSelected: "Vargur"})
    }
    chooseKronos = () => {
        this.setState({shipSelected: "Kronos"})
    }
    chooseRevelation = () => {
        this.setState({shipSelected: "Revelation"})
    }
    choosePhoenix = () => {
        this.setState({shipSelected: "Phoenix"})
    }
    chooseMoros = () => {
        this.setState({shipSelected: "Moros"})
    }
    chooseNaglfar = () => {
        this.setState({shipSelected: "Naglfar"})
    }
    chooseGila = () => {
        this.setState({shipSelected: "Gila"})
    }
    choosePraxis = () => {
        this.setState({shipSelected: "Praxis"})
    }
    chooseNestor = () => {
        this.setState({shipSelected: "Nestor"})
    }
    chooseLeshak = () => {
        this.setState({shipSelected: "Leshak"})
    }
    chooseRattlesnake = () => {
        this.setState({shipSelected: "Rattlesnake"})
    }
    chooseHeron = () => {
        this.setState({shipSelected: "Heron"})
        return (                <div>
            <Heatmap shipSelected = {this.state.shipSelected}/>
            </div>)
    }
    compressor = () => {
        this.setState({isCompressed: true})
    }
    render() {
        return(
        this.state.isCompressed ?  (
            <div>
            <button id = 'shipSelector' className = 'button1' onClick = {this.handleClick}>Select Targets &#8594;</button>
            <div>
            <Heatmap shipSelected = {this.state.shipSelected}/>
            </div>
            </div>
        ) :
        (
            <div>
                <button className = 'button1' onClick = {this.compressor}>&#8592;</button>
                <button className = 'button1' onClick = {this.chooseGolem}>Golem</button>
                <button className = 'button1' onClick = {this.choosePaladin}>Paladin</button>
                <button className = 'button1' onClick = {this.chooseVargur}>Vargur</button>
                <button className = 'button1' onClick = {this.chooseKronos}>Kronos</button>
                <button className = 'button1' onClick = {this.chooseRevelation}>Revelation</button>
                <button className = 'button1' onClick = {this.choosePhoenix}>Phoenix</button>
                <button className = 'button1' onClick = {this.chooseMoros}>Moros</button>
                <button className = 'button1' onClick = {this.chooseNaglfar}>Naglfar</button>
                <button className = 'button1' onClick = {this.chooseGila}>Gila</button>
                <button className = 'button1' onClick = {this.choosePraxis}>Praxis</button>
                <button className = 'button1' onClick = {this.chooseNestor}>Nestor</button>
                <button className = 'button1' onClick = {this.chooseLeshak}>Leshak</button>
                <button className = 'button1' onClick = {this.chooseRattlesnake}>Rattlesnake</button>
                <button className = 'button1' onClick = {this.chooseHeron}>Heron</button>
                <Heatmap shipSelected = {this.state.shipSelected}/>
            </div>
        )
        )
    }
}

export default ShipSelector;
