import React from 'react'

class Tuesday extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            expanded: false,
            content: <p className = 'noPad'>Tuesday</p>
        }
    };
    handleHover = () =>{
        this.setState({expanded: true})
    }



    render(){
        if(this.state.expanded === false){
            return(
                <div className = 'Weekday' onMouseOver = {this.handleHover}>
                    {this.state.content}
                </div>
                )
        } else {
            return (
                <div>
                    <ul className = 'Weekday'>
                        <li>00:00 - 02:59</li>
                        <h1></h1>
                        <li>03:00 - 05:59</li>
                        <h1></h1>
                        <li>06:00 - 08:59</li>
                        <h1></h1>
                        <li>09:00 - 11:59</li>
                        <h1></h1>
                        <li>12:00 - 14:59</li>
                        <h1></h1>
                        <li>15:00 - 17:59</li>
                        <h1></h1>
                        <li>18:00 - 20:59</li>
                        <h1></h1>
                        <li>21:00 - 23:59</li>
                        <h1></h1>
                    </ul>
                </div>
            )
        }
    }
}

export default Tuesday
